const input = $input.first().json;

const nowIso = new Date().toISOString();
const startedMs = Date.now();

const tool_name = input.tool_name || 'hubspot.billing';
const dry_run = (input.dry_run === undefined || input.dry_run === null) ? true : !!input.dry_run;

const safeJson = (obj) => {
  const s = JSON.stringify(obj ?? {});
  return s.replace(/'/g, "''");
};

const q = (v) => (v === undefined || v === null || v === '') ? null : String(v);

const qSql = (v) => {
  const s = q(v);
  if (s === null) return 'null';
  return `'${s.replace(/'/g, "''")}'`;
};

const nSql = (v) => {
  if (v === undefined || v === null || v === '') return 'null';
  const num = Number(v);
  return Number.isFinite(num) ? String(num) : 'null';
};

const uuidSql = (v) => {
  const s = (v ?? '').toString().trim();
  const ok = /^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i.test(s);
  return ok ? `'${s}'::uuid` : 'null';
};

const note_scope = 'hubspot';
const note_key = input.invoice_id ? `invoice.${input.invoice_id}` : (input.subscription_id ? `subscription.${input.subscription_id}` : 'billing.general');

const payload_for_log = {
  ...input,
  tool_name,
  dry_run,
  note_scope,
  note_key,
  started_at: nowIso,
  _started_ms: startedMs
};

const log_start_sql = `
INSERT INTO tool_execution_log (
  tool_name,
  input_data,
  success,
  conversation_id,
  user_id,
  required_approval
)
VALUES (
  ${qSql(tool_name)},
  '${safeJson(payload_for_log)}'::jsonb,
  false,
  ${uuidSql(input.conversation_id)},
  ${nSql(input.user_id)},
  ${dry_run ? 'false' : 'true'}
)
RETURNING id;
`;

const ctx_sql = `
SELECT jsonb_build_object(
  'agent_notes', (
    SELECT COALESCE(jsonb_agg(n ORDER BY n.updated_at DESC), '[]'::jsonb)
    FROM agent_notes n
    WHERE n.scope = ${qSql(note_scope)}
      AND n.note_key = ${qSql(note_key)}
    LIMIT 15
  ),
  'schema_cache', (
    SELECT to_jsonb(s)
    FROM hubspot_schema_cache s
    WHERE s.portal_id = ${nSql(input.hs_portal_id)}
    ORDER BY s.fetched_at DESC
    LIMIT 1
  )
) AS context;
`;

return [{
  json: {
    ...payload_for_log,
    log_start_sql,
    ctx_sql
  }
}];

const base = $('Normalize + Build SQL').first().json;

const logStart = $('Log Start (tool_execution_log)').first().json;
const tool_execution_id = (logStart && logStart.id) ? logStart.id : null;

const ctxRows = $('Load Context').first().json;
const context_pack = (ctxRows && ctxRows.context) ? ctxRows.context : {};

return [{
  json: {
    ...base,
    tool_execution_id,
    context_pack
  }
}];

const base = $('Assemble Context Pack').first().json;

const started = Number(base._started_ms || Date.now());
const duration_ms = Math.max(0, Date.now() - started);

let agentRaw = $('AI Agent - Billing').first().json;

const coerceJson = (val) => {
  if (val === null || val === undefined) return null;
  if (typeof val === 'object') return val;
  if (typeof val !== 'string') return { value: val };
  const s = val.trim().replace(/^```json\s*/i, '').replace(/```$/,'').trim();
  try { return JSON.parse(s); } catch { return { raw: val }; }
};

const agentOut = coerceJson(agentRaw.output ?? agentRaw.text ?? agentRaw);

const ok = !(agentOut?.errors && agentOut.errors.length);

const esc = (s) => String(s ?? '').replace(/'/g, "''");

const final = {
  ok,
  tool_name: base.tool_name,
  tool_execution_id: base.tool_execution_id,
  session_id: base.session_id,
  conversation_id: base.conversation_id,
  dry_run: base.dry_run,
  summary: agentOut?.summary ?? (ok ? 'ok' : 'error'),
  data: agentOut?.data ?? null,
  tool_calls: agentOut?.tool_calls ?? [],
  errors: agentOut?.errors ?? [],
  notes_to_save: agentOut?.notes_to_save ?? null,
  context_used: base.context_pack ?? {},
  duration_ms
};

const outJson = esc(JSON.stringify(final));
const errText = final.errors?.length ? esc(JSON.stringify(final.errors)) : null;

final.log_end_sql = `
UPDATE tool_execution_log
SET
  output_data = '${outJson}'::jsonb,
  success = ${final.ok ? 'true' : 'false'},
  error = ${errText ? `'${errText}'` : 'null'},
  duration_ms = ${Number.isFinite(final.duration_ms) ? final.duration_ms : 'null'}
WHERE id = '${esc(base.tool_execution_id)}'::uuid;
`;

const note_scope = base.note_scope || 'hubspot';
const note_key = base.note_key || 'billing.general';

if (final.notes_to_save && typeof final.notes_to_save === 'string' && final.notes_to_save.trim() !== '') {
  const noteObj = { text: final.notes_to_save.trim() };
  const noteJson = esc(JSON.stringify(noteObj));
  final.save_notes_sql = `
INSERT INTO agent_notes (scope, note_key, note)
VALUES ('${esc(note_scope)}', '${esc(note_key)}', '${noteJson}'::jsonb);
`;
}

return [{ json: final }];
