// Build Google Docs SOP Template with Category Folder Routing
const input = $json;

// Category to folder mapping (matches Google_Drive_SOP_Folder_Setup)
const categoryFolderMap = {
  'Master': '01_MASTER_DOCUMENTS',
  'Business': '02_Business_Operations',
  'Business Operations': '02_Business_Operations',
  'Target Market': '03_Target_Market',
  'ICP': '03_Target_Market',
  'Services': '04_Services_Products',
  'Products': '04_Services_Products',
  'Enrichment': '05_Enrichment_Pipeline',
  'HubSpot': '06_HubSpot_CRM',
  'CRM': '06_HubSpot_CRM',
  'Outreach': '07_Outreach_Sequences',
  'Voice': '08_Voice_AI',
  'Voice AI': '08_Voice_AI',
  'Retell': '08_Voice_AI',
  'Infrastructure': '09_Infrastructure',
  'Integration': '10_Integrations',
  'Agent': '11_AI_Agents',
  'Knowledge': '12_Knowledge_Base',
  'Standards': '13_Standards_Guidelines',
  'Guidelines': '13_Standards_Guidelines',
  'Template': '14_Templates',
  'Analytics': '15_Analytics_Reports',
  'Archive': '16_Archive',
  'General': '09_Infrastructure'  // Default
};

// Format the document title
const docTitle = `[${input.category}] ${input.sopName} - v${input.version}`;
const targetFolder = categoryFolderMap[input.category] || '09_Infrastructure';

// Build the document content as plain text (Google Docs will format)
const content = `
================================================================================
                        AUTO SHOP MEDIA - STANDARD OPERATING PROCEDURE
================================================================================

SOP ID: ${input.sopId}
SOP Name: ${input.sopName}
Category: ${input.category}
Status: ${input.status}
Priority: ${input.priority}
Version: ${input.version}
Owner: ${input.owner}
Created: ${input.createdAt}
Tags: ${input.tags}

--------------------------------------------------------------------------------
                                    QUICK SUMMARY
--------------------------------------------------------------------------------
${input.quickSummary || 'No summary provided.'}

--------------------------------------------------------------------------------
                                      PURPOSE
--------------------------------------------------------------------------------
${input.purpose || 'Purpose not defined.'}

--------------------------------------------------------------------------------
                                       SCOPE
--------------------------------------------------------------------------------
${input.scope || 'Scope not defined.'}

--------------------------------------------------------------------------------
                                  PREREQUISITES
--------------------------------------------------------------------------------
${input.prerequisites || 'No prerequisites.'}

--------------------------------------------------------------------------------
                                 PROCEDURE STEPS
--------------------------------------------------------------------------------
${input.steps || 'Steps not defined.'}

--------------------------------------------------------------------------------
                                EXPECTED OUTCOME
--------------------------------------------------------------------------------
${input.expectedOutcome || 'Expected outcome not defined.'}

--------------------------------------------------------------------------------
                                 TROUBLESHOOTING
--------------------------------------------------------------------------------
${input.troubleshooting || 'No troubleshooting information.'}

--------------------------------------------------------------------------------
                               RELATED WORKFLOWS
--------------------------------------------------------------------------------
${input.relatedWorkflows || 'No related workflows.'}

================================================================================
                              END OF DOCUMENT
================================================================================

This SOP was auto-generated by the Auto Shop Media automation system.
Last updated: ${input.createdAt}
`;

return [{
  json: {
    ...input,
    docTitle,
    docContent: content,
    targetFolder
  }
}];
