// Aggregate all competitor intelligence
const items = $input.all();

// Get competitor info from first item
const competitor = items[0]?.json?.competitor || {};

// Find place details (from SerpAPI)
const placeDetails = items.find(i => i.json?.body?.place_results)?.json?.body?.place_results || {};

// Find reviews
const reviewsData = items.find(i => i.json?.body?.reviews)?.json?.body || {};
const reviews = reviewsData.reviews || [];

// Find website content
const websiteData = items.find(i => i.json?.body?.data?.markdown)?.json?.body?.data || {};

// Calculate metrics
const avgRating = placeDetails.rating || 0;
const totalReviews = placeDetails.reviews || 0;
const recentReviews = reviews.slice(0, 10);

// Sentiment analysis on recent reviews
const sentimentCounts = { positive: 0, neutral: 0, negative: 0 };
recentReviews.forEach(r => {
  if (r.rating >= 4) sentimentCounts.positive++;
  else if (r.rating >= 3) sentimentCounts.neutral++;
  else sentimentCounts.negative++;
});

// Extract pricing signals from website
const websiteMarkdown = websiteData.markdown || '';
const pricingKeywords = websiteMarkdown.match(/\$\d+|free|affordable|cheap|premium|discount|deal|special|offer/gi) || [];
const serviceKeywords = websiteMarkdown.match(/oil change|brake|tire|alignment|diagnostic|engine|transmission|ac repair|inspection/gi) || [];

// Calculate competitive threat score (0-100)
let threatScore = 0;
threatScore += Math.min(avgRating * 10, 50); // Up to 50 points for rating
threatScore += Math.min(totalReviews / 100, 25); // Up to 25 points for review volume
threatScore += sentimentCounts.positive > sentimentCounts.negative ? 15 : 0; // 15 for positive sentiment
threatScore += pricingKeywords.length > 3 ? 10 : 0; // 10 for active pricing/promotions

const intelligence = {
  monitor_id: competitor.monitor_id,
  competitor_name: competitor.competitor_name,
  competitor_place_id: competitor.competitor_place_id,
  our_company_id: competitor.our_company_id,
  scraped_at: new Date().toISOString(),

  // Place details
  place_data: {
    name: placeDetails.title || competitor.competitor_name,
    address: placeDetails.address,
    phone: placeDetails.phone,
    website: placeDetails.website,
    hours: placeDetails.hours,
    type: placeDetails.type,
    price_level: placeDetails.price
  },

  // Reviews intelligence
  review_data: {
    avg_rating: avgRating,
    total_reviews: totalReviews,
    recent_reviews: recentReviews.map(r => ({
      rating: r.rating,
      text: r.snippet || r.text,
      date: r.date,
      author: r.user?.name
    })),
    sentiment: sentimentCounts,
    review_velocity: totalReviews > 0 ? (totalReviews / 12) : 0 // Approx monthly
  },

  // Website intelligence
  website_data: {
    has_website: !!websiteData.markdown,
    content_length: websiteMarkdown.length,
    pricing_signals: [...new Set(pricingKeywords)],
    services_mentioned: [...new Set(serviceKeywords)],
    has_online_booking: /book|schedule|appointment/i.test(websiteMarkdown),
    has_specials: /special|deal|discount|coupon/i.test(websiteMarkdown)
  },

  // Competitive analysis
  competitive_analysis: {
    threat_score: Math.round(threatScore),
    threat_level: threatScore >= 70 ? 'high' : threatScore >= 40 ? 'medium' : 'low',
    strengths: [],
    weaknesses: []
  }
};

// Identify strengths and weaknesses
if (avgRating >= 4.5) intelligence.competitive_analysis.strengths.push('Excellent ratings');
if (totalReviews > 500) intelligence.competitive_analysis.strengths.push('High review volume');
if (intelligence.website_data.has_online_booking) intelligence.competitive_analysis.strengths.push('Online booking available');
if (intelligence.website_data.has_specials) intelligence.competitive_analysis.strengths.push('Active promotions');

if (avgRating < 4) intelligence.competitive_analysis.weaknesses.push('Below-average ratings');
if (totalReviews < 50) intelligence.competitive_analysis.weaknesses.push('Low review volume');
if (!intelligence.website_data.has_website) intelligence.competitive_analysis.weaknesses.push('No/poor website');
if (sentimentCounts.negative > sentimentCounts.positive) intelligence.competitive_analysis.weaknesses.push('Negative sentiment trend');

return [{ json: intelligence }];

